import React, { useState } from 'react';
import axios from 'axios';
import './EmailForm.css';

const EmailForm = ({ showNotification }) => {
  const [formData, setFormData] = useState({
    to: '',
    subject: '',
    message: '',
  });
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Validar formulario
    if (!formData.to || !formData.subject || !formData.message) {
      showNotification('Todos los campos son requeridos', 'error');
      return;
    }

    setLoading(true);

    try {
      const response = await axios.post(
        `${process.env.REACT_APP_API_URL}/send-email`,
        formData
      );
      
      if (response.data.success) {
        showNotification('Email enviado correctamente!', 'success');
        // Limpiar formulario
        setFormData({ to: '', subject: '', message: '' });
      } else {
        showNotification('Error al enviar el email', 'error');
      }
    } catch (error) {
      console.error('Error:', error);
      showNotification(
        error.response?.data?.message || 'Error al conectar con el servidor', 
        'error'
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="email-form-container">
      <h2>Nuevo correo</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="to">Destinatario:</label>
          <input
            type="email"
            id="to"
            name="to"
            value={formData.to}
            onChange={handleChange}
            placeholder="correo@ejemplo.com"
          />
        </div>
        
        <div className="form-group">
          <label htmlFor="subject">Asunto:</label>
          <input
            type="text"
            id="subject"
            name="subject"
            value={formData.subject}
            onChange={handleChange}
            placeholder="Asunto del correo"
          />
        </div>
        
        <div className="form-group">
          <label htmlFor="message">Mensaje:</label>
          <textarea
            id="message"
            name="message"
            value={formData.message}
            onChange={handleChange}
            placeholder="Escribe tu mensaje aquÃ­"
            rows="6"
          />
        </div>
        
        <button 
          type="submit" 
          className="send-button" 
          disabled={loading}
        >
          {loading ? 'Enviando...' : 'Enviar correo'}
        </button>
      </form>
    </div>
  );
};

export default EmailForm;
