import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './UserForm.css';

const UserForm = ({ showNotification }) => {
  const [formData, setFormData] = useState({
    nombre: '',
    email: '' // Cambiado de telefono a email
  });
  const [usuarios, setUsuarios] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    // Cargar la lista de usuarios al montar el componente
    fetchUsuarios();
  }, []);

  const fetchUsuarios = async () => {
    try {
      const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:5000';
      const response = await axios.get(`${apiUrl}/usuarios`);
      setUsuarios(response.data);
    } catch (error) {
      console.error('Error al cargar usuarios:', error);
      showNotification('Error al cargar la lista de usuarios', 'error');
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Validar formato de email
  const validateEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Validar formulario
    if (!formData.nombre || !formData.email) {
      showNotification('Nombre y email son requeridos', 'error');
      return;
    }

    // Validar formato de email
    if (!validateEmail(formData.email)) {
      showNotification('El formato del email no es válido', 'error');
      return;
    }

    setLoading(true);

    try {
      const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:5000';
      const response = await axios.post(
        `${apiUrl}/usuarios`,
        formData
      );
      
      showNotification('Usuario registrado correctamente!', 'success');
      // Limpiar formulario
      setFormData({ nombre: '', email: '' });
      // Actualizar la lista de usuarios
      fetchUsuarios();
      
    } catch (error) {
      console.error('Error detallado:', error);
      
      let errorMessage = 'Error al conectar con el servidor';
      
      if (error.response) {
        errorMessage = error.response.data.error || 
                      `Error ${error.response.status}: ${error.response.statusText}`;
      } else if (error.request) {
        errorMessage = 'No se recibió respuesta del servidor';
      }
      
      showNotification(errorMessage, 'error');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="email-form-container">
      <h2>Registro de Usuarios</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="nombre">Nombre:</label>
          <input
            type="text"
            id="nombre"
            name="nombre"
            value={formData.nombre}
            onChange={handleChange}
            placeholder="Ingrese su nombre"
          />
        </div>
        
        <div className="form-group">
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            placeholder="Ingrese su correo electrónico"
          />
        </div>
        
        <button 
          type="submit" 
          className="send-button" 
          disabled={loading}
        >
          {loading ? 'Registrando...' : 'Registrar Usuario'}
        </button>
      </form>

      <div className="usuarios-list">
        <h3>Usuarios Registrados</h3>
        {usuarios.length > 0 ? (
          <ul>
            {usuarios.map((usuario, index) => (
              <li key={index}>
                <strong>{usuario.nombre}</strong> - {usuario.email}
              </li>
            ))}
          </ul>
        ) : (
          <p>No hay usuarios registrados todavía.</p>
        )}
      </div>
    </div>
  );
};

export default UserForm;
