import React, { useState } from 'react';
import './App.css';
import EmailForm from './components/EmailForm';
import UserForm from './components/UserForm';
import Notification from './components/Notification';

function App() {
  const [notification, setNotification] = useState({ show: false, message: '', type: '' });
  const [activeForm, setActiveForm] = useState('email'); // 'email' o 'user'

  const showNotification = (message, type) => {
    setNotification({ show: true, message, type });
    
    // Ocultar la notificación después de 5 segundos
    setTimeout(() => {
      setNotification({ show: false, message: '', type: '' });
    }, 5000);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Sistema de Gestión</h1>
        <div className="navigation-tabs">
          <button 
            className={activeForm === 'email' ? 'active' : ''} 
            onClick={() => setActiveForm('email')}
          >
            Envío de Correos
          </button>
          <button 
            className={activeForm === 'user' ? 'active' : ''} 
            onClick={() => setActiveForm('user')}
          >
            Usuarios
          </button>
        </div>
      </header>
      <main>
        {notification.show && (
          <Notification 
            message={notification.message} 
            type={notification.type} 
          />
        )}
        
        {activeForm === 'email' ? (
          <EmailForm showNotification={showNotification} />
        ) : (
          <UserForm showNotification={showNotification} />
        )}
      </main>
    </div>
  );
}

export default App;
